import React, {Component} from 'react';
import './style.css';

class Square extends Component {
    render() {
        return (
        <button 
            className="square"
            onClick={() => { 
                this.setState((prevState) => {
                    return {
                        value: prevState.value === "X" ? "O": "X",
                    }

                })    
            }}
        >
            {this.props.value}
        </button>
        );
    }
}

export default Square;
