import React, {Component} from 'react';
import './style.css';

class Board extends Component {
    renderSquare(i) {
        var highlight = false;
        console.log(this.props)
        for(let index = 0; index < this.props.line.length; index ++) {
            if(i === this.props.line[index]){
                highlight = true;
            }
        }
        return (<Square 
            value={this.props.squares[i]} 
            onClick={() => {this.props.onClick(i)}}
            highlight = {highlight}
        />);
    }
    render() {
        const arr =[[0, 1, 2]];
        return (
        <div>
            <div className="status">{this.props.status}</div>
            {
                arr.map((item) => {
                    return this.renderSquare(item)
                })
            }
            <div className="board-row">
            {this.renderSquare(3)}
            {this.renderSquare(4)}
            {this.renderSquare(5)}
            </div>
            <div className="board-row">
            {this.renderSquare(6)}
            {this.renderSquare(7)}
            {this.renderSquare(8)}
            </div>
        </div>
        );
    }      
}

//只包含 render 方法且沒有自己 state 的 component 適合寫成 function component 
function Square(props) {
    return (
        <button className= {props.highlight ? "highlight" : "square" } onClick={props.onClick}>
            {props.value}
        </button>
    );
}

export default Board;
