import React, {Component} from 'react';
import './style.css';

class Board extends Component {
    renderSquare(i) {
        return <Square 
        value={this.props.squares[i]} 
        onClick={() => {this.props.onClick(i)}}
        />;
    }
    handleClick(i) {
        const squares = this.state.squares.slice();
        if (calWin(squares) || squares[i]) {
            return;
        }
        squares[i] = this.state.xIsNext ? "X" : "O";
        this.setState(() => {
            return {
                squares: squares, 
                xIsNext: !this.state.xIsNext,
            }
        })
    }
    render() {
        const winner = calWin(this.state.squares);
        let status;
        if (winner) {
            status = "Winner: " + winner;
        } else {
            status = 'Next player: ' + (this.state.xIsNext ? "X" : "O");
        }
        return (
        <div>
            <div className="status">{status}</div>
            <div className="board-row">
            {this.renderSquare(0)}
            {this.renderSquare(1)}
            {this.renderSquare(2)}
            </div>
            <div className="board-row">
            {this.renderSquare(3)}
            {this.renderSquare(4)}
            {this.renderSquare(5)}
            </div>
            <div className="board-row">
            {this.renderSquare(6)}
            {this.renderSquare(7)}
            {this.renderSquare(8)}
            </div>
        </div>
        );
    }      
}

//只包含 render 方法且沒有自己 state 的 component 適合寫成 function component 
function Square(props) {
    return (
        <button className="square" onClick={props.onClick}>
            {props.value}
        </button>
    );
}

function calWin(squares){
    const lines = [
        [0, 1, 2],
        [3, 4, 5],
        [6, 7, 8],
        [0, 3, 6],
        [1, 4, 7],
        [2, 5, 8],
        [0, 4, 8],
        [2, 4, 6]
    ];
    for (let i = 0; i < lines.length; i++) {
        const line = lines[i];
        if (squares[line[0]] !== null && squares[line[1]] === squares[line[0]] && squares[line[2]] === squares[line[0]])
            return squares[line[0]];
    }
    return null;    
}

export default Board;
