import React, {Component} from 'react';
import './style.css';

class Board extends Component {
    constructor(props){
        super(props);
        this.state = {
            squares: Array(9).fill(null),
        }
    }
    renderSquare(i) {
        return <Square 
        value={this.state.squares[i]} 
        onClick={() => {this.handleClick(i)}}
        />;
    }
    handleClick(i) {
        const squares = this.state.squares.slice();
        squares[i] = "X";
        this.setState(() => {
            return {
                squares: squares, 
            }
        })
    }
    render() {
        const status = 'Next player: X';

        return (
        <div>
            <div className="status">{status}</div>
            <div className="board-row">
            {this.renderSquare(0)}
            {this.renderSquare(1)}
            {this.renderSquare(2)}
            </div>
            <div className="board-row">
            {this.renderSquare(3)}
            {this.renderSquare(4)}
            {this.renderSquare(5)}
            </div>
            <div className="board-row">
            {this.renderSquare(6)}
            {this.renderSquare(7)}
            {this.renderSquare(8)}
            </div>
        </div>
        );
    }      
}

//
function Square(props) {
    return (
        <button className="square" onClick={props.onClick}>
            {props.value}
        </button>
    );
}
export default Board;
